  --js-accent-background-color: fade(@yellow, 30%);
  --http-accent-background-color: fade(@green, 30%);
  --apis-accent-background-color: fade(@mauve, 30%);
  --learn-accent-background-color: fade(@pink, 30%);
  --plus-accent-engage: fade(@maroon, 70%);
  --html-accent-engage: fade(@red, 70%);
  --css-accent-engage: fade(@sapphire, 70%);
  --js-accent-engage: fade(@yellow, 70%);
  --http-accent-engage: fade(@green, 70%);
  --apis-accent-engage: fade(@mauve, 70%);
  --learn-accent-engage: fade(@pink, 70%);
  --modal-backdrop-color: fade(@mantle, 70%);
  --selection-background-color: fade(@surface2, 60%);
