  --baseline-high-engine-bg: hsl(
    hue(@green),
    saturation(@green),
    lightness(@base)
  );
  --baseline-high-check: @green;
  --baseline-low-bg: hsl(hue(@blue), saturation(@blue), lightness(@surface0));
