@-moz-document domain("m.youtube.com") {
  html[darker-dark-theme] {
    #catppuccin(@darkFlavor);
  }
  html {
    #catppuccin(@lightFlavor);
  }

  #catppuccin(@flavor) {
    #lib.palette();

    background-color: @base !important;
    color: @text !important;

    /* Ambient mode */
    #cinematics {
      mix-blend-mode: lighten;
    }

    .mobile-topbar-header[data-mode="watch"],
    ytm-mobile-topbar-renderer:not([ambient-topbar]) {
      background: @base;
    }

    .mobile-topbar-header[data-mode="watch"] .mobile-topbar-logo,
    .mobile-topbar-header[data-mode="watch"] .mobile-topbar-title,
    .mobile-topbar-header[data-mode="watch"] .mobile-topbar-header-content {
      color: @text;
    }

    .yt-spec-bottom-sheet-layout__bottom-sheet-renderer-container,
    .modern-styling {
      background: @surface0;
    }

    .yt-spec-bottom-sheet-layout__bottom-sheet-layout-header-wrapper {
      border-bottom-color: @surface0;
    }

    .yt-spec-bottom-sheet-layout__bottom-sheet-content {
      color: @text;
    }

    /* Comment box */
    .engagement-panel-container {
      background: @mantle;
    }

    /* Bottom bar */
    ytm-pivot-bar-renderer {
      background: @mantle;
      color: @text;
      border-top-color: @surface0;
    }

    /* Chips */
    .chip-bar {
      background-color: @base;
    }

    [chip-style*="STYLE_"] {
      .chip-container {
        color: @text;
        background-color: @surface0;
      }

      &.selected .chip-container {
        color: @crust;
        background-color: @accent;
      }
    }

    /* Buttons */

    .yt-spec-touch-feedback-shape--touch-response {
      .yt-spec-touch-feedback-shape__fill {
        background-color: @text;
      }

      .yt-spec-touch-feedback-shape__stroke {
        border-color: @surface1;
      }
    }

    .yt-spec-touch-feedback-shape--overlay-touch-response-inverse
      .yt-spec-touch-feedback-shape__fill {
      background-color: @accent;
    }
    .yt-spec-touch-feedback-shape--overlay-touch-response
      .yt-spec-touch-feedback-shape__fill {
      background-color: @text;
    }
  }
}

/* ==UserStyle==
@name Reddit Catppuccin
@namespace github.com/catppuccin/userstyles/styles/reddit
@homepageURL https://github.com/catppuccin/userstyles/tree/main/styles/reddit
@version 2025.09.06
@updateURL https://github.com/catppuccin/userstyles/raw/main/styles/reddit/catppuccin.user.less
@supportURL https://github.com/catppuccin/userstyles/issues?q=is%3Aopen+is%3Aissue+label%3Areddit
@description Soothing pastel theme for Reddit
@author Catppuccin
@license MIT

@preprocessor less
@var select lightFlavor "Light Flavor" ["latte:Latte*", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha"]
@var select darkFlavor "Dark Flavor" ["latte:Latte", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha*"]
@var select accentColor "Accent" ["rosewater:Rosewater", "flamingo:Flamingo", "pink:Pink", "mauve:Mauve*", "red:Red", "maroon:Maroon", "peach:Peach", "yellow:Yellow", "green:Green", "teal:Teal", "blue:Blue", "sapphire:Sapphire", "sky:Sky", "lavender:Lavender", "subtext0:Gray"]
==/UserStyle== */

@import "https://userstyles.catppuccin.com/lib/lib.less";

@-moz-document domain("reddit.com") {
  :root:not(.theme-dark) .theme-rpl,
  :root:not(.theme-dark).theme-rpl,
  :root:not(.theme-dark) .theme-beta,
  :root:not(.theme-dark).theme-beta,
  .theme-light {
    #catppuccin(@lightFlavor);
  }

  :root:not(.theme-light) .theme-rpl,
  :root:not(.theme-light).theme-rpl,
  :root:not(.theme-light) .theme-beta,
  :root:not(.theme-light).theme-beta,
  .theme-dark {
    #catppuccin(@darkFlavor);
  }

  #catppuccin(@flavor) {
    #lib.palette();
    #lib.defaults();

    /* BETA */
    --color-pizzaRed: @red !important;
    --color-global-brand-orangered: @peach !important;
    --color-global-focus: @surface1 !important;
    --color-interactive-content-disabled: @subtext0 !important;
    --color-interactive-background-disabled: fade(@surface2, 70%) !important;
    --color-interactive-pressed: @surface2 !important;
    --color-neutral-content: @text !important;
    --color-neutral-content-disabled: @subtext0 !important;
    --color-neutral-content-weak: @subtext0 !important;
    --color-neutral-content-strong: @text !important;
    --color-global-white: @crust !important;
