  /* Root */
  :root:has(.central-featured) {
    #catppuccin(@lightFlavor);

    &, body {
      color: @text;
      background-color: @base;

      .svg-Wikipedia_wordmark {
        filter: @text-filter;
      }

      .central-featured-lang small,
      .other-project-tagline,
      .site-license,
      .footer-sidebar-text {
        color: @subtext1;
      }

      .central-featured-lang :hover {
        background-color: @surface1;
      }

      .other-project-link:hover {
        background-color: @surface1;
      }

      .search-container {
        #searchInput {
          color: @text;
          background-color: @mantle;
          border-color: @overlay2;
          box-shadow: none;

          &:focus {
            border-color: @accent;
          }
        }

        .js-langpicker-label {
          color: @subtext0;
        }
        .styled-select:hover {
          background-color: @surface1;
        }
        .sprite.svg-arrow-down {
          filter: @text-filter;
        }

        button[type="submit"] {
          background-color: @accent;
          border-color: @accent;

          .svg-search-icon {
            filter: @crust-filter;
          }
        }

        .suggestions-dropdown {
          background-color: @mantle;
          border-color: @overlay2;

          .suggestion-link {
            border-bottom-color: @overlay0;

            &.active {
              background-color: fade(@accent, 10%);
            }

            .suggestion-thumbnail {
              @svg: escape(
                '<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 56 56"><path fill="@{surface1}" d="M0 0h56v56H0z"/><path fill="@{overlay2}" d="M36.4 13.5H17.8v24.9c0 1.4.9 2.3 2.3 2.3h18.7v-25c.1-1.4-1-2.2-2.4-2.2M30.2 17h5.1v6.4h-5.1zm-8.8 0h6v1.8h-6zm0 4.6h6v1.8h-6zm0 15.5v-1.8h13.8v1.8zm13.8-4.5H21.4v-1.8h13.8zm0-4.7H21.4v-1.8h13.8z"/></svg>',
              );
              background-image: url("data:image/svg+xml,@{svg}");
            }

            .suggestion-title {
              color: @text;
            }
          }
        }
      }

      hr {
        border-bottom-color: @surface1;
      }

      .lang-list-border {
        background-color: @surface2;
      }
      .lang-list-button {
        background-color: @mantle;
        color: @accent;
        outline-color: @mantle;
        border-color: @surface2;

        .svg-language-icon,
        .svg-arrow-down-blue {
          filter: @accent-filter;
        }

        &:focus {
          box-shadow: inset 0 0 0 1px @accent;
        }
      }
      .lang-list-container {
        background-color: @surface0;

        .bookshelf {
          border-top-color: @overlay1;
          box-shadow: none;

          .text {
            color: @text;
            background-color: @surface2;
          }
        }
      }
    }
  }
}

/* ==UserStyle==
@name Google Gemini Catppuccin
@namespace github.com/catppuccin/userstyles/styles/google-gemini
@homepageURL https://github.com/catppuccin/userstyles/tree/main/styles/google-gemini
@version 2026.01.04
@updateURL https://github.com/catppuccin/userstyles/raw/main/styles/google-gemini/catppuccin.user.less
@supportURL https://github.com/catppuccin/userstyles/issues?q=is%3Aopen+is%3Aissue+label%3Agoogle-gemini
@description Soothing pastel theme for Google Gemini
@author Catppuccin
@license MIT

@preprocessor less
@var select lightFlavor "Light Flavor" ["latte:Latte*", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha"]
@var select darkFlavor "Dark Flavor" ["latte:Latte", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha*"]
@var select accentColor "Accent" ["rosewater:Rosewater", "flamingo:Flamingo", "pink:Pink", "mauve:Mauve*", "red:Red", "maroon:Maroon", "peach:Peach", "yellow:Yellow", "green:Green", "teal:Teal", "blue:Blue", "sapphire:Sapphire", "sky:Sky", "lavender:Lavender", "subtext0:Gray"]
==/UserStyle== */

@import "https://userstyles.catppuccin.com/lib/lib.less";

@-moz-document domain("gemini.google.com") {
  @import url("https://unpkg.com/@catppuccin/highlightjs@1.0.0/css/catppuccin-variables.important.css");

  :root .dark-theme {
    #catppuccin(@darkFlavor);
  }

  :root .light-theme {
